import React, { useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import styles from "./SignUpPage.module.css";

const SignUpPage = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleNameChange = (e) => setName(e.target.value);
  const handleEmailChange = (e) => setEmail(e.target.value);
  const handlePasswordChange = (e) => setPassword(e.target.value);
  const handleConfirmPasswordChange = (e) =>
    setConfirmPassword(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Name:", name);
    console.log("Email:", email);
    console.log("Password:", password);
    console.log("Confirm Password:", confirmPassword);
  };

  return (
    <div
      className="position-relative d-flex justify-content-center align-items-center"
      style={{
        height: "100vh",
        backgroundImage: "url(https://www.iliketowastemytime.com/sites/default/files/canoe-mountain-view-hd-wallpaper.jpg)",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
      }}
    >
      <div
        className="position-absolute"
        style={{ top: "75px", left: "50%", transform: "translateX(-50%)" }}
      >
        <img
          className={styles.logo}
          src={require("../../assets/RapidLogoV2.png")}
          alt="Logo"
        />
      </div>
      <div
        className="position-absolute bg-white rounded p-3 p-md-5"
        style={{ opacity: 0.9, boxShadow: "0px 0px 30px 0px rgba(0, 0, 0, 0.8)" }}
      >
        <Container>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formBasicName" className="mt-1">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter name"
                value={name}
                onChange={handleNameChange}
              />
            </Form.Group>

            <Form.Group controlId="formBasicEmail" className="mt-1">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter email"
                value={email}
                onChange={handleEmailChange}
              />
            </Form.Group>

            <Form.Group controlId="formBasicPassword" className="mt-1">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Password"
                value={password}
                onChange={handlePasswordChange}
              />
            </Form.Group>

            <Form.Group controlId="formBasicConfirmPassword" className="mt-1">
              <Form.Label>Confirm Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={handleConfirmPasswordChange}
              />
            </Form.Group>

            <div className="d-flex justify-content-center mt-3">
              <Button variant="primary" type="submit">
                Sign Up
              </Button>
            </div>
          </Form>
        </Container>
      </div>
    </div>
  );
};

export default SignUpPage;
